import React from 'react'
import {HuePicker} from 'react-color'
import './App.css';

class App extends React.Component {
  state={
    select:'hex',
    color:'',
    addForm:{
      id:1,
      name:'',
      type:'',
      color: '',
    },
    table: [
      {id:1, name:'name1', type: 'main', color: '#fff'},
      {id:2, name:'name1', type: 'main', color: '#fff'}
    ]

  }
  // addForm:{...this.state.addForm, 
  huePickerChange = (color)=>{
    this.setState({color:color}, ()=> console.log(this.state.color.rgb))
    if (this.state.select == 'hex'){
    this.setState({addForm:{...this.state.addForm, color:color.hex}}, ()=> console.log(this.state.addForm.color.rgb))
    }
    else if(this.state.select == 'rgb'){
      this.setState({addForm:{...this.state.addForm, color:`r: ${color.rgb.r} g: ${color.rgb.g} b: ${color.rgb.b} a: ${color.rgb.a}` }})
    }
  }

  selectChange =(event)=>{
    this.setState({select:event.target.value})
    console.log(event.target.value)
    if(event.target.value == 'rgb'){
      const{r,g,b,a} = this.state.color.rgb
      this.setState({addForm:{...this.state.addForm,color:`r: ${r} g: ${g} b: ${b} a: ${a}` }})
    }
    else{
      this.setState({addForm:{...this.state.addForm,color: this.state.color.hex}})
    }
  }

  changeAddString = (event)=>{
    var inputName = event.target.name;
    var inputValue = event.target.value;
    this.setState({addForm:{...this.state.addForm, [inputName]:inputValue}},() => console.log(this.state.addForm))
}

  addStringToTable = (event) =>{
    event.preventDefault()
    this.setState({table:[...this.state.table, {...this.state.addForm}]}, ()=> console.log(this.state.table))
    this.setState({addForm:{...this.state.addForm, id:id++}})
  }

  render(){
    return (
      <div className="App">
      <h1 className='title'>Редактор табличных данных</h1>
      <form className='add_form'>
        <p onChange={this.changeAddString} className='add_form_item' >Имя<input name='name'/></p>
        <p onChange={this.changeAddString} className='add_form_item'>Тип<input name='type'/></p>
        <p onChange={this.changeAddString} className='add_form_item'>Цвет<input value={this.state.addForm.color && this.state.addForm.color} name='color'/></p>
        <select onChange={this.selectChange}>
          <option>hex</option>
          <option>rgb</option>
        </select>
        <HuePicker color={this.state.addForm.color} onChange={this.huePickerChange} className='add_form_item'/>
        <button onClick={this.addStringToTable}>Добавить</button>
      </form>
      <ul>
        {this.state.table.map((el) => <li key={this.state.id}>{el.name} {el.type} {el.color}<button>Удалить</button> </li>)}
      </ul>

      </div>
    );
  }
}

export default App;
