import React from 'react'
import './App.css';

class App extends React.Component {
  state=[
    {id:1, name:'name1', type: 'main', color: '#fff'},
    {id:2, name:'name1', type: 'main', color: '#fff'}
  ]

  render(){
    return (
      <div className="App">
      <h1 className='title'>Редактор табличных данных</h1>
      <form className='add_String'>
        <p>Имя</p><input name='name'/>
        <p>Тип</p><input name='type'/>
        <p>Цвет</p><input name='color'/>

        <button>Добавить</button>
      </form>
      <ul>
        {this.state.map((el) => <li key={this.state.id}>{this.state.name} {this.state.type} {this.state.color} </li>)}
      </ul>

      </div>
    );
  }
}

export default App;
