import React from 'react'
import {HuePicker} from 'react-color'
import './App.css';

class App extends React.Component {
  state={
    addForm:{
      id:'',
      name:'',
      type:'',
      color: '',
    },
    table: [
      {id:1, name:'name1', type: 'main', color: '#fff'},
      {id:2, name:'name1', type: 'main', color: '#fff'}
    ]

  }

  handleChange = (color)=>{
    this.setState({addForm:{...this.state.addForm, color:color}}, ()=> console.log(this.state.addForm.color.rgb))
  }

  selectChange =(event)=>{
    console.log(event.target)
  }

  render(){
    return (
      <div className="App">
      <h1 className='title'>Редактор табличных данных</h1>
      <form className='add_string'>
        <p className='add_string_item'>Имя<input name='name'/></p>
        <p className='add_string_item'>Тип<input name='type'/></p>
        <p className='add_string_item'>Цвет<input value={this.state.addForm.color.rgb && this.state.addForm.color.rgb.toString()} name='color'/></p>
        <select onChange={this.selectChange}>
          <option>hex</option>
          <option>rgb</option>
        </select>
        <HuePicker color={this.state.addForm.color} onChange={this.handleChange} className='add_string_item'/>
        <button>Добавить</button>
      </form>
      <ul>
        {this.state.table.map((el) => <li key={this.state.id}>{el.name} {el.type} {el.color}<button>Удалить</button> </li>)}
      </ul>

      </div>
    );
  }
}

export default App;
