import React from 'react'
import {HuePicker} from 'react-color'
import './App.css';

class App extends React.Component {
  state=[
    render [
      {id:1, name:'name1', type: 'main', color: '#fff'},
      {id:2, name:'name1', type: 'main', color: '#fff'}
    ]

  ]

  render(){
    return (
      <div className="App">
      <h1 className='title'>Редактор табличных данных</h1>
      <form className='add_string'>
        <p className='add_string_item'>Имя<input name='name'/></p>
        <p className='add_string_item'>Тип<input name='type'/></p>
        <p className='add_string_item'>Цвет<input name='color'/></p>
        <HuePicker className='add_string_item'/>
        <button>Добавить</button>
      </form>
      <ul>
        {this.state.map((el) => <li key={this.state.id}>{el.name} {el.type} {el.color}<button>Удалить</button> </li>)}
      </ul>

      </div>
    );
  }
}

export default App;
